document.addEventListener("DOMContentLoaded",()=>{let e=document.querySelectorAll(".fade-in-up, .fade-in-down, .fade-in-left, .fade-in-right"),t=new IntersectionObserver(e=>{e.forEach(e=>{e.isIntersecting&&(e.target.classList.add("visible"),t.unobserve(e.target))})},{threshold:.1,rootMargin:"0px 0px -50px 0px"});e.forEach(e=>{t.observe(e)})});